package package2;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day=1;
		switch(day)
		{
		case 1:
			System.out.println("day is monday");
			break;
		case 2:
			System.out.println("day is tuesday");
			break;
		case 3:
			System.out.println("day is wednesday");
			break;
		case 4:
			System.out.println("not a vaid day");
		}

	}

}
